import React, { useState } from 'react';

import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import Card from "../UI/Card";
import ExpensesFilter from "../ExpensesFilter/ExpensesFilter";



const Expenses = (props) => {

  const [filteredYear, setFilterYear] = useState('2021');

  const filterChangeHandler = (selectedYear) => {
    setFilterYear(selectedYear);
  };
  const fillteredYearByYear = props.data.filter((expense) => (expense.date.getFullYear().toString() ===filteredYear))

  return (
    <div>

      <Card className="expenses">
      <ExpensesFilter selected={filteredYear} onChangeFilter={filterChangeHandler}/>
        {fillteredYearByYear.length() === 0 ? (<p>There is no expense for this year!</p> ) : (fillteredYear.map((expense) => 
        (<ExpenseItem
          key= {expense.id} 
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />)))};
      </Card>
    </div>
  );
};

export default Expenses;
